package com.viraj.Converter;

public class Converter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float farenheit = 65f;
		System.out.println(farenheit);
		
		farenheit = (((farenheit - 32) / 9) * 5);
		System.out.println(farenheit);
		
		int a, b, c, z = 5;
		a = b = c = z;
		
		System.out.println(a + " " + b + " " + c + " " + z);
		

	}

}
